
public class Operacion {

	public Operacion() {
		// TODO Auto-generated constructor stub
	}
	
	public void suma(float num_uno, float num_dos){
		System.out.println( "\nLa suma es: " + (num_uno + num_dos ));
	}
	
	public void resta(float num_uno, float num_dos){
		System.out.println( "\nLa resta es: " + (num_uno - num_dos ));
	}
	
	public void producto(float num_uno, float num_dos){
		System.out.println( "\nEl producto es: " + num_uno * num_dos );
	}
	
	public void division(float num_uno, float num_dos){
		System.out.println( "\nLa división es: " + num_uno / num_dos );
	}

}
